/// @title OldAngleRouterPolygon
/// @author Angle Core Team
/// @notice Router contract built specifially for Angle use cases on Polygon
contract OldAngleRouterPolygon is OldBaseAngleRouterSidechain {
    using SafeERC20 for IERC20;

    IWETH9 public constant WMATIC = IWETH9(0x0d500B1d8E8eF31E21C99d1Db9A6444d3ADf1270);

    function initializeRouter(
        address _core,
        address _uniswapRouter,
        address _oneInch
    ) public {
        _initialize(_core);
        uniswapV3Router = IUniswapV3Router(_uniswapRouter);
        oneInch = _oneInch;
    }

    /// @inheritdoc OldBaseAngleRouterSidechain
    function _wrap(uint256, uint256) internal pure override returns (uint256) {
        return 0;
    }

    /// @inheritdoc OldBaseAngleRouterSidechain
    function _unwrap(
        uint256,
        uint256,
        address
    ) internal pure override returns (uint256) {
        return 0;
    }

    /// @inheritdoc OldBaseAngleRouterSidechain
    function _wrapNative() internal override returns (uint256) {
        WMATIC.deposit{ value: msg.value }();
        return msg.value;
    }

    /// @inheritdoc OldBaseAngleRouterSidechain
    function _unwrapNative(uint256 minAmountOut, address to) internal override returns (uint256 amount) {
        amount = WMATIC.balanceOf(address(this));
        _slippageCheck(amount, minAmountOut);
        if (amount > 0) {
            WMATIC.withdraw(amount);
            _safeTransferNative(to, amount);
        }
        return amount;
    }
}

contracts/mock/MockSavingsRateIlliquid.s.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.12;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC4626.sol";

// This mock only works for USDC as an asset
contract MockSavingsRateIlliquid is ERC4626 {
    mapping(address => uint256) public receiverRewards;
    uint256 public splitFactor;
    mapping(address => uint256) public counter;

    constructor(IERC20Metadata asset_) ERC20("savingsRate", "sr") ERC4626(asset_) {}

    function setSplitFactor(uint256 _splitFactor) external {
        splitFactor = _splitFactor;
    }

    function setReceiverRewards(address receiver, uint256 rewards) external {
        receiverRewards[receiver] = rewards;
    }

    function claimRedeem(address receiver, address[] memory strategiesToClaim) external returns (uint256 totalOwed) {
        totalOwed = receiverRewards[receiver];
        for (uint256 i = 0; i < strategiesToClaim.length; ++i) {
            counter[strategiesToClaim[i]] += 1;
        }
        IERC20Metadata(asset()).transfer(receiver, totalOwed);
    }

    function prepareRedeem(
        uint256 shares,
        address receiver,
        address owner
    ) external returns (uint256 assets) {
        if (msg.sender != owner) {
            _spendAllowance(owner, msg.sender, shares);
        }
        _burn(owner, shares);
        // divide by 10**9 * 10**(18-6)
        assets = (shares * (10**9 - splitFactor)) / 10**21;
        receiverRewards[receiver] = shares / 10**12 - assets;
        IERC20Metadata(asset()).transfer(receiver, assets);
    }
}

